package EatAdvisor;

import javafx.scene.Scene;
import javafx.scene.control.Label;
import javafx.scene.image.Image;
import javafx.scene.image.ImageView;
import javafx.scene.layout.Pane;
import javafx.scene.paint.Paint;

import java.io.ByteArrayInputStream;
import java.util.Base64;

public class ThemeManager {
//    private static final String LIGHT_THEME_PATH = ".." + File.separator + ".." + File.separator + "res" + File.separator + "light.css";
//    private static final String DARK_THEME_PATH = ".." + File.separator + ".." + File.separator + "res" + File.separator + "dark.css";

    private static final String MOON_ICON_BASE_64 = /*"data:image/png;base64," + */"";
//    private static final String SUN_ICON_BASE64 = /*"data:image/png;base64," + */"";
    private static final String SUN_ICON_BASE64 = /*"data:image/png;base64," + */"";

    private static final byte[] MOON_ICON_BYTEARRAY = Base64.getDecoder().decode(MOON_ICON_BASE_64);
    private static final byte[] SUN_ICON_BYTEARRAY = Base64.getDecoder().decode(SUN_ICON_BASE64);

    public static final Image moonIcon = new Image(new ByteArrayInputStream(MOON_ICON_BYTEARRAY),50,50,true,true);
    public static final Image sunIcon = new Image(new ByteArrayInputStream(SUN_ICON_BYTEARRAY),50,50,true,true);

    private String lightThemeUrl; // TODO: CSS
    private String darkThemeUrl;

    private static ThemeManager themeManager;
    private boolean dark;

    public static ThemeManager getThemeManager() {
        if (themeManager == null) {
            themeManager = new ThemeManager();
        }
        return themeManager;
    }

    private ThemeManager () {
        dark = true;
        themeManager = this;
    }

    // TODO: aggiungere qualcosa che carichi un css di default
    //  (basta rimuovere il css assegnato ai FXML e caricarne uno da qui)

    public void changeTheme(Pane pane, ImageView imageView) {
        if (dark) {
            dark = false;
            imageView.setImage(moonIcon);
            pane.getStylesheets().clear();
            pane.getStylesheets().add("light.css");
//            Scene.getStylesheets()
//                    .add(getClass().getResource(skinFileName).toExternalForm());
        } else {
            dark = true;
            imageView.setImage(sunIcon);
            pane.getStylesheets().clear();
            pane.getStylesheets().add("dark.css");
        }
    }

    public boolean isDark() {
        return dark;
    }

    public boolean alert(Label l, boolean okay) {
        if (dark) {
            // tema scuro
            if (okay) {
                l.setTextFill(Paint.valueOf("00FF9F"));
                return true;
            } else {
                l.setTextFill(Paint.valueOf("D600FF"));
                return false;
            }
        } else {
            // tema chiaro (old)
            if (okay) {
                l.setTextFill(Paint.valueOf("000000"));
                return true;
            } else {
                l.setTextFill(Paint.valueOf("FF0000"));
                return false;
            }
        }
    }
}
